import React, {useEffect, useState} from 'react';
import ReactMarkdown from 'react-markdown';
import axios from 'axios';
import imageClosing from '../images/nagy-arnold-X_IvVDuHvDQ-unsplash.jpg';
import quotesClosing from '../images/”.png';
import imageTemoignage from '../images/karina-vargas-J7hCnavj7QE-unsplash.jpg';
import Carousel from 'react-elastic-carousel';


function ClosingAcademie() {
    const [isLoading, setIsLoading] = useState(true);
    const [articles, setArticles] = useState(null);
    const [temoignages, setTemoignages] = useState(null);
    const [files,setFiles] = useState()

    const [articleAcademie, setArticleAcademie] = useState({});

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            //POST IMAGE
            const formData = new FormData();
            formData.append('files', files[0])

            await axios.post('http://localhost:1337/api/upload/', formData)
            .then((response) => {
                console.log(response)
            }).catch((error) => {
                console.log(error);
            })

            // POST ARTICLE ACADEMIE
            const {data} = await axios.post('http://localhost:1337/api/articles-academies', {
                data: {
                    titleAcademie: articleAcademie.titleAcademie,
                    contentAcademie: articleAcademie.contentAcademie,
                }
            })
            console.log(data)
        } catch(error) {
            console.log(error);
        }

    }

    const handleChange = ({ currentTarget }) => {
        const {name, value} = currentTarget;
        setArticleAcademie({
            ...articleAcademie,
            [name]: value,
        })
    } 

    useEffect(() => {
        GetArticlesClosingAcademie();
    },[])

    function GetArticlesClosingAcademie() {
        const axiosRequest1 = axios.get('http://localhost:1337/api/articles-academies');
        const axiosRequest2 = axios.get('http://localhost:1337/api/temoignages');
        axios.all([axiosRequest1, axiosRequest2])
        .then(axios.spread(function(data1, data2) {
            setArticles(data1.data.data);
            setTemoignages(data2.data.data);
            setIsLoading(false);
            //console.log(data1.data.data);
            // console.log(data2.data.data[0].attributes.contentTemoignage);
        }))
        .catch(error => {
            console.log(error)
        })
    }

    return (
        <div>
            <nav>
                <div className="navbar">
                    <div className="logo-sesame">
                        <h1>Sésame œuvre-toi</h1>
                    </div>
                    <div className="icon-navbar">
                        <i class="fas fa-bars"></i>
                    </div>                
                </div>
                <div className="navbar-desktop">
                    <div className="logo-sesame">
                        <h1>Sésame œuvre-toi</h1>
                    </div>
                    <div>
                        <ul>
                            <li>À propos</li>
                            <li>Closing</li>
                            <li>Chronique lifestyle</li>
                            <li>Oeuvre-toi</li>
                            <li>Contact</li>
                        </ul>                    
                    </div>                    
                </div>
            </nav>

            <div className="page-closing">
                <section className="first-part-closing">
                    <div className="img-title-closing">
                        <h2>J’ouvre les portes de l’académie</h2>
                        <img src={imageClosing} alt="image d'arbrisseau" id="image-academie" />
                    </div>
                    <div className="list-closing-academie">
                        <div className="cadre-white"></div>
                        <ul>
                        {
                            isLoading ? 'Loading...' : articles.map(article => <a href={`#${article.attributes.titleAcademie}`}><li key={article.attributes.id}>{article.attributes.titleAcademie}</li></a>)
                        }
                            {/*<Link to={article.attributes.id}><li id="li-academie">L’Académie</li></Link> */}
                            <a href="#temoignages"><li>Témoignages</li></a>
                        </ul>
                    </div>
                </section>

                <section className="articles-closing">
                <form onSubmit={handleSubmit}>
                    <label>Image</label>
                    <input type="file" id="image" name="imageAcademie" onChange={(e)=>setFiles(e.target.files)} />
                    <label>Titre</label>
                    <input type="text" id="title" name="titleAcademie" onChange={handleChange} />
                    <label>Contenu</label>
                    <textarea type="text" id="content" name="contentAcademie" onChange={handleChange} />
                    <input type="submit" value="Envoyer" class="submit-form" />
                </form>
                    <ul>
                        {
                            isLoading ? 'Loading...' : articles.map(article => <li key={article.attributes.id}><h3 id={`${article.attributes.titleAcademie}`}>{article.attributes.titleAcademie}</h3><img src={article.attributes.imageAcademie} alt="#" /><ReactMarkdown className="markdown-academie">{article.attributes.contentAcademie}</ReactMarkdown></li>)
                        }                                 
                    </ul>
                </section>

                <section className="temoignages" id="temoignages">
                    <h3>Témoignages</h3>
                    <div className="display-flex-temoignage">
                        <div>
                            <img src={imageTemoignage} className="img-temoignage" alt="image temoignage chemin" />
                        </div>
                        <div className="display-temoignages">
                            <img src={quotesClosing} id="img-quotes" alt="image guillemets" />
                            {
                                isLoading ? 'Loading...' : <Carousel className="carousel-temoignages">{temoignages.map(temoignage => <p key={temoignage.attributes.id}>{temoignage.attributes.contentTemoignage}</p>)}</Carousel>
                            }                                
                        </div>
                    </div>
                </section>             
            </div>          
        </div>
    )
}

export default ClosingAcademie;
