import React, {useEffect, useState} from 'react';
import ReactMarkdown from 'react-markdown';
import { Link } from "react-router-dom";
import axios from 'axios';
import Carousel from 'react-elastic-carousel';
import carouselImage from '../images/ana-frantz-azRS34AdaaM-unsplash.jpg';
import articleDetails from '../images/devin-avery-BRVqq2uak4E-unsplash.jpg';
import arbreVie from '../images/—Pngtree—celtic life tree decoration pattern_6814309.png';
import principalImage from '../images/stefan-stefancik-QXevDflbl8A-unsplash.jpg';

function Oeuvretoi() {
    const [isLoading, setIsLoading] = useState(true);
    const [articles, setArticles] = useState([]);

    const [articleOeuvre, setArticleOeuvre] = useState({});


    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const {data} = axios.post('http://localhost:1337/api/articles-oeuvres', {
                data: {
                    titleOeuvre: articleOeuvre.titleOeuvre,
                    contentOeuvre: articleOeuvre.contentOeuvre,               
                }
                })
                console.log(data);
        } catch(error) {
            console.log(error);
        }
    }

    const handleChange = ({ currentTarget }) => {
        const {name, value} = currentTarget;
        setArticleOeuvre({
            ...articleOeuvre,
            [name]: value,
        })
    } 

    useEffect(() => {
        GetArticlesOeuvre();
    },[])

    function GetArticlesOeuvre() {
        axios.get('http://localhost:1337/api/articles-oeuvres')
        .then((response) => {
            setIsLoading(false);
            setArticles(response.data.data);
            console.log(response.data.data)
        })
        .catch(error => {
            console.log(error);
        })
    }

    const displaySelectClosing = async () => {
        let select = document.querySelector(".select-navbar");

        if(select.style.display === "inline-block") {
            select.style.display = "none";
        } else {
            select.style.display = "inline-block";
        }
    }

    return (
        <div>
            <nav>
                <div className="navbar">
                    <div className="logo-sesame">
                        <Link to="/home"><h1>Sésame œuvre-toi</h1></Link>
                    </div>
                    <div className="icon-navbar">
                        <i class="fas fa-bars"></i>
                    </div>                
                </div>
                <div className="navbar-desktop">
                    <div className="logo-sesame">
                        <Link to="/home"><h1>Sésame œuvre-toi</h1></Link>
                    </div>
                    <div>
                        <ul>
                            <Link to="/apropos"><li>À propos</li></Link>
                            <div>
                                <li id="closing-list-nav" onClick={displaySelectClosing}>Closing ▿</li>
                                <div className="select-navbar">
                                    <ul name="closings" id="closings">
                                        <Link to="/academie"><li>J'ouvre les portes de l'académie</li></Link>
                                        <Link to="/jedecouvre"><li>Je découvre</li></Link>
                                    </ul>
                                </div>
                            </div>
                            <Link to="/chronique"><li>Chronique lifestyle</li></Link>
                            <Link to="/oeuvretoi"><li>Oeuvre-toi</li></Link>
                            <Link to="/contact"><li>Contact</li></Link>
                        </ul>                    
                    </div>                    
                </div>
            </nav>

            <div className="oeuvre-toi">
                <section className="chronique-first-part">
                    <div className="card-article-principal-oeuvre">
                        <div>
                            <h1>Oeuvre-toi</h1>
                            <img src={arbreVie} alt="image qui illustre un arbre celtique" />
                        </div>
                        <div className="image-media-oeuvre-toi">
                            <img src={principalImage} alt="image principale de la page oeuvre-toi" />
                        </div>
                    </div>

                    {/* HORIZONTAL CAROUSEL */}
                    <div className="horizontal-carousel-chronique">
                        {
                            isLoading ? "Loading..." :
                            <Carousel className="cards-vertical-carousel">
                                {articles.map((article) => 
                                    <a href={`#${article.attributes.titleOeuvre}`}>
                                        <div className="card-vertical" id="card-vertical-2">
                                            <div>
                                                <img className="img-vertical-carousel" src={carouselImage} alt="" />
                                            </div>
                                            <div className="text-vertical">
                                                <h3>{article.attributes.titleOeuvre}</h3>
                                                <ReactMarkdown>{article.attributes.contentOeuvre}</ReactMarkdown>
                                            </div>
                                        </div>
                                    </a>
                                )}
                            </Carousel>                     
                        }
                    </div>

                    {/* VERTICAL CAROUSEL */}
                    <div className="vertical-carousel-chronique">
                        {
                            isLoading ? "Loading..." :
                            <Carousel verticalMode itemsToShow={3} className="cards-vertical-carousel">
                                {articles.map((article) =>
                                <a href={`#${article.attributes.titleOeuvre}`}>
                                    <div key={article.id} className="card-vertical" id="card-vertical-2">
                                        <div>
                                            <img className="img-vertical-carousel" src={carouselImage} alt="" />
                                        </div>
                                        <div className="text-vertical">
                                            <h3>{article.attributes.titleOeuvre}</h3>
                                            <p>{article.attributes.contentOeuvre}</p>
                                        </div>
                                    </div>
                                </a>
                                )}
                            </Carousel>
                        }
                    </div>
                </section>

                <hr className="display-hr" />
                <hr className="display-hr-media"/>

                <form onSubmit={handleSubmit}>
                    <label>Titre</label>
                    <input type="text" id="titleOeuvre" name="titleOeuvre" onChange={handleChange} />
                    <label>Contenu</label>
                    <textarea type="text" id="contentOeuvre" name="contentOeuvre" onChange={handleChange} />
                    <input type="submit" value="Envoyer" class="submit-form" />
                </form>

                {
                    isLoading ? "Loading..." : 
                    articles.map((article) =>
                    <Link to={`/chronique/${article.id}`}>
                        <section className="oeuvre-article-details">
                            <h3 id={article.attributes.titleOeuvre}>{article.attributes.titleOeuvre}</h3>
                            <img src={articleDetails} alt="image qui illustre le détail de l'article" />
                            <ReactMarkdown>{article.attributes.contentOeuvre}</ReactMarkdown>
                        </section>
                    </Link>
                    ) 
                }
            </div>
        </div>
    )
}

export default Oeuvretoi;
