import React, {useEffect, useState} from 'react';
import ReactMarkdown from 'react-markdown';
import { Link } from "react-router-dom";
import axios from 'axios';
import imageClosing from '../images/nagy-arnold-X_IvVDuHvDQ-unsplash.jpg';

function Closingjedecouvre() {
    const [isLoading, setIsLoading] = useState(true);
    const [articles, setArticles] = useState(null);
    const [temoignages, setTemoignages] = useState(null);
    const [files,setFile] = useState(false)

    const [articleAcademie, setArticleAcademie] = useState({});


    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const formData = new FormData()
            formData.append('files', files);

            await axios.post('http://localhost:1337/api/upload', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
              });
        } catch(error) {
            console.log(error);
        }

        try {
            const {data} = axios.post('http://localhost:1337/api/articles-academies', {
                data: {
                    titleAcademie: articleAcademie.titleAcademie,
                    contentAcademie: articleAcademie.contentAcademie,
                    imageAcademie: files.files                
                }
                })
                console.log(data);
        } catch(error) {
            console.log(error);
        }
    }

    const handleChange = ({ currentTarget }) => {
        const {name, value} = currentTarget;
        setArticleAcademie({
            ...articleAcademie,
            [name]: value,
        })
        setFile({
            ...files,
            [name]: value
        })
    } 

    useEffect(() => {
        GetArticlesClosingAcademie();
    },[])

    function GetArticlesClosingAcademie() {
        const axiosRequest1 = axios.get('http://localhost:1337/api/articles-academies');
        const axiosRequest2 = axios.get('http://localhost:1337/api/temoignages');
        axios.all([axiosRequest1, axiosRequest2])
        .then(axios.spread(function(data1, data2) {
            setArticles(data1.data.data);
            setTemoignages(data2.data.data);
            setIsLoading(false);
            console.log(data1.data.data[0]);
            console.log(data1.data.data[0].attributes.imageAcademie.data[0].attributes.url);
            // console.log(data2.data.data[0].attributes.contentTemoignage);
        }))
        .catch(error => {
            console.log(error)
        })
    }

    return (
        <div>
            <nav>
                <div className="navbar">
                    <div className="logo-sesame">
                        <h1>Sésame œuvre-toi</h1>
                    </div>
                    <div className="icon-navbar">
                        <i class="fas fa-bars"></i>
                    </div>                
                </div>
                <div className="navbar-desktop">
                    <div className="logo-sesame">
                        <h1>Sésame œuvre-toi</h1>
                    </div>
                    <div>
                        <ul>
                            <Link to="/apropos"><li>À propos</li></Link>
                            <Link to="/academie"><li>Closing</li></Link>
                            <Link to="/chronique"><li>Chronique lifestyle</li></Link>
                            <Link to="/apropos"><li>Oeuvre-toi</li></Link>
                            <Link to="/contact"><li>Contact</li></Link>
                        </ul>                    
                    </div>                    
                </div>
            </nav>

            <div className="page-closing">
                <section className="first-part-closing">
                    <div>
                        <h2>Je découvre</h2>
                        <img src={imageClosing} alt="image d'arbrisseau"  />
                    </div>
                    <div className="list-closing">
                        <div className="cadre-white"></div>
                        <ul>
                            <a href=""><li>Qu'est-ce que le closing ?</li></a>
                            <a href=""><li>Pourquoi le closing ?</li></a>
                            <a href=""><li>Avantages et inconvénients du closing</li></a>
                            <a href=""><li>Questions/Réponses</li></a>
                        </ul>
                    </div>
                </section>

                <section className="articles-closing">
                    <h3>Qu'est-ce que le closing ?</h3>
                    <p>"Sed ut perspiciatis unde omnis iste natus error sit voluptatem accusantium doloremque laudantium, totam rem aperiam, eaque ipsa quae ab illo inventore veritatis et quasi architecto beatae vitae dicta sunt explicabo. Nemo enim ipsam voluptatem quia voluptas sit aspernatur aut odit aut fugit, sed quia consequuntur magni dolores eos qui ratione voluptatem sequi nesciunt. Neque porro quisquam est, qui dolorem ipsum quia dolor sit amet, consectetur, adipisci velit, sed quia non numquam eius modi tempora incidunt ut labore et dolore magnam aliquam quaerat voluptatem. 

Ut enim ad minima veniam, quis nostrum exercitationem ullam corporis suscipit laboriosam, nisi ut aliquid ex ea commodi consequatur? Quis autem vel eum iure reprehenderit qui in ea voluptate velit esse quam nihil molestiae consequatur, vel illum qui dolorem eum fugiat quo voluptas nulla pariatur?"</p>
                </section>                
            </div>

        </div>
    )
}

export default Closingjedecouvre;
