import React, {useEffect, useState} from 'react';
import ReactMarkdown from 'react-markdown';
import { Link } from "react-router-dom";
import axios from 'axios';
import Carousel from 'react-elastic-carousel';
import carouselImage from '../images/ana-frantz-azRS34AdaaM-unsplash.jpg';
import articlePrincipal from '../images/xps-8pb7Hq539Zw-unsplash.jpg';
import articleDetails from '../images/devin-avery-BRVqq2uak4E-unsplash.jpg';

function Chronique() {
    const [isLoading, setIsLoading] = useState(true);
    const [articles, setArticles] = useState(null);
    const [temoignages, setTemoignages] = useState(null);
    const [files,setFile] = useState(false)

    const [articleAcademie, setArticleAcademie] = useState({});


    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const formData = new FormData()
            formData.append('files', files);

            await axios.post('http://localhost:1337/api/upload', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
              });
        } catch(error) {
            console.log(error);
        }

        try {
            const {data} = axios.post('http://localhost:1337/api/articles-academies', {
                data: {
                    titleAcademie: articleAcademie.titleAcademie,
                    contentAcademie: articleAcademie.contentAcademie,
                    imageAcademie: files.files                
                }
                })
                console.log(data);
        } catch(error) {
            console.log(error);
        }
    }

    const handleChange = ({ currentTarget }) => {
        const {name, value} = currentTarget;
        setArticleAcademie({
            ...articleAcademie,
            [name]: value,
        })
        setFile({
            ...files,
            [name]: value
        })
    } 

    useEffect(() => {
        GetArticlesClosingAcademie();
    },[])

    function GetArticlesClosingAcademie() {
        const axiosRequest1 = axios.get('http://localhost:1337/api/articles-academies');
        const axiosRequest2 = axios.get('http://localhost:1337/api/temoignages');
        axios.all([axiosRequest1, axiosRequest2])
        .then(axios.spread(function(data1, data2) {
            setArticles(data1.data.data);
            setTemoignages(data2.data.data);
            setIsLoading(false);
            console.log(data1.data.data[0]);
            console.log(data1.data.data[0].attributes.imageAcademie.data[0].attributes.url);
            // console.log(data2.data.data[0].attributes.contentTemoignage);
        }))
        .catch(error => {
            console.log(error)
        })
    }

    return (
        <div>
            <nav>
                <div className="navbar">
                    <div className="logo-sesame">
                        <h1>Sésame œuvre-toi</h1>
                    </div>
                    <div className="icon-navbar">
                        <i class="fas fa-bars"></i>
                    </div>                
                </div>
                <div className="navbar-desktop">
                    <div className="logo-sesame">
                        <h1>Sésame œuvre-toi</h1>
                    </div>
                    <div>
                        <ul>
                            <Link to="/apropos"><li>À propos</li></Link>
                            <Link to="/academie"><li>Closing</li></Link>
                            <Link to="/chronique"><li>Chronique lifestyle</li></Link>
                            <Link to="/apropos"><li>Oeuvre-toi</li></Link>
                            <Link to="/contact"><li>Contact</li></Link>
                        </ul>                    
                    </div>                    
                </div>
            </nav>

            <div className="chronique">
                <section className="chronique-first-part">
                    <div className="card-article-principal">
                        <h1>Chronicle Lifestyle</h1>
                        <img src={articlePrincipal} alt="image qui illustre le dernier article" />
                        <h3>Lorem ipsum dolor sit amet, consectetur adipiscing elit</h3>
                    </div>
                    <hr className="display-hr" />

                    {/* HORIZONTAL CAROUSEL */}
