import React, {useEffect, useState} from 'react';
import axios from 'axios';
import articlesAcademieAPI from '../javascript/articlesAcademieAPI';
import Carousel from 'react-elastic-carousel';
import imageClosing from '../images/nagy-arnold-X_IvVDuHvDQ-unsplash.jpg';
import quotesClosing from '../images/”.png';
import imageTemoignage from '../images/karina-vargas-J7hCnavj7QE-unsplash.jpg';


function ClosingAcademie() {
    const [isLoading, setIsLoading] = useState(true);
    const [articles, setArticles] = useState(null);

    const [articleAcademie, setArticleAcademie] = useState({});


    const handleChange = ({ currentTarget }) => {
        const {name, value} = currentTarget;
        setArticleAcademie({
            ...articleAcademie,
            [name]: value,
        })
    }

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const {data} = articlesAcademieAPI();
            console.log(data)
        } catch(error) {
            console.log(error);
        }

    }

    useEffect(() => {
        GetArticlesClosingAcademie();
    },[])

    function GetArticlesClosingAcademie() {
        axios.get('http://localhost:1337/api/articles-academies')
        .then((response) => {
            setArticles(response.data.data)
            setIsLoading(false)
            // console.log(response.data.data[0].attributes.title)
        })
        .catch(error => {
            console.log(error)
        })
    }

    return (
        <div>
            <nav>
                <div className="navbar">
                    <div className="logo-sesame">
                        <h1>Sésame œuvre-toi</h1>
                    </div>
                    <div className="icon-navbar">
                        <i class="fas fa-bars"></i>
                    </div>                
                </div>
                <div className="navbar-desktop">
                    <div className="logo-sesame">
                        <h1>Sésame œuvre-toi</h1>
                    </div>
                    <div>
                        <ul>
                            <li>À propos</li>
                            <li>Closing</li>
                            <li>Chronique lifestyle</li>
                            <li>Oeuvre-toi</li>
                            <li>Contact</li>
                        </ul>                    
                    </div>                    
                </div>
            </nav>

            <div className="page-closing">
                <section className="first-part-closing">
                    <div>
                        <h2>J’ouvre les portes de l’académie</h2>
                        <img src={imageClosing} alt="image d'arbrisseau" id="image-academie" />
                    </div>
                    <div className="list-closing-academie">
                        <div className="cadre-white"></div>
                        <ul>
                            <a href=""><li id="li-academie">L’Académie</li></a>
                            <a href=""><li>La formation à l’académie française de closing</li></a>
                            <a href=""><li>À qui s’adresse cette formation ?</li></a>
                            <a href=""><li>Mon avis sur le contenu de la formation</li></a>
                            <a href=""><li>Témoignages</li></a>
                        </ul>
                    </div>
                </section>

                <section className="articles-closing">
                <form onSubmit={handleSubmit}>
                    <label>Titre</label>
                    <input type="text" id="title" name="titleAcademie" onChange={handleChange} />
                    <label>Contenu</label>
                    <textarea type="text" id="content" name="contentAcademie" onChange={handleChange}/>
                    <input type="submit" value="Envoyer" class="submit-form" />
                </form>
                    <ul>
                        <li>
                            <h3>L’Académie</h3>
                            {
                                isLoading ? 'Loading...' : articles.map(article => <p key={article.attributes.id}>{article.attributes.contentAcademie}</p>)
                            }                                     
                        </li>
                    </ul>
                </section>

                <section className="temoignages">
                    <h3>Témoignages</h3>
                    <div className="display-flex-temoignage">
                        <div>
                            <img src={imageTemoignage} className="img-temoignage" alt="image temoignage chemin" />
                        </div>
                        <div className="display-temoignages">
                            <img src={quotesClosing} id="img-quotes" alt="image guillemets" />  
                            <Carousel className="carousel-temoignages">
                                <p>Ut enim ad minima veniam, quis nostrum exercitationem ullam corporis suscipit laboriosam, nisi ut aliquid ex ea commodi consequatur? Quis autem vel eum iure reprehenderit qui in ea voluptate velit esse quam nihil molestiae consequatur, vel illum qui dolorem eum fugiat quo voluptas nulla pariatur?</p>
                                <p>Ut enim ad minima veniam, quis nostrum exercitationem ullam corporis suscipit laboriosam, nisi ut aliquid ex ea commodi consequatur? Quis autem vel eum iure reprehenderit qui in ea voluptate velit esse quam nihil molestiae consequatur, vel illum qui dolorem eum fugiat quo voluptas nulla pariatur?</p>
                            </Carousel>
                        </div>
                    </div>
                </section>             
            </div>          
        </div>
    )
}

export default ClosingAcademie;
