import React, {useEffect, useState} from 'react';
import axios from 'axios';
import { Link } from "react-router-dom";
import imgPropos from '../images/nishaan-ahmed-mDQM7TmmcD4-unsplash.jpg';

function Apropos() {
    const [isLoading, setIsLoading] = useState(true);
    const [articlesApropos, setArticlesApropos] = useState(null);

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const {data} = axios.post('http://localhost:1337/api/articles-apropos', {
                data: {
                    contentAcademie: articlesApropos.contentApropos,              
                }
                })
                console.log(data);
        } catch(error) {
            console.log(error);
        }
    }

    const handleChange = ({ currentTarget }) => {
        const {name, value} = currentTarget;
        setArticlesApropos({
            ...articlesApropos,
            [name]: value,
        })
    } 

    useEffect(() => {
        GetArticlesApropos();
    },[])

    function GetArticlesApropos() {
        axios.get('http://localhost:1337/api/articles-apropos')
        .then((data) => {
            setArticlesApropos(data.data.data);
            setIsLoading(false);
        })
        .catch(error => {
            console.log(error);
        })
    }

    return (
        <div>
            <nav>
                <div className="navbar">
                    <div className="logo-sesame">
                        <h1>Sésame œuvre-toi</h1>
                    </div>
                    <div className="icon-navbar">
                        <i class="fas fa-bars"></i>
                    </div>                
                </div>
                <div className="navbar-desktop">
                    <div className="logo-sesame">
                        <h1>Sésame œuvre-toi</h1>
                    </div>
                    <div>
                        <ul>
                            <Link to="/apropos"><li>À propos</li></Link>
                            <Link to="/academie"><li>Closing</li></Link>
                            <Link to="/chronique"><li>Chronique lifestyle</li></Link>
                            <Link to="/apropos"><li>Oeuvre-toi</li></Link>
                            <Link to="/contact"><li>Contact</li></Link>
                        </ul>                    
                    </div>                    
                </div>
            </nav>

            <section className="page-a-propos">
                <div>
                    <h1>À propos de moi</h1>
                    <img src={imgPropos} alt="" />                    
                </div>
                <form onSubmit={handleSubmit}>
                    <label>Contenu</label>
                    <textarea type="text" id="content" name="contentAcademie" onChange={handleChange} />
                    <input type="submit" value="Envoyer" class="submit-form" />
                </form>
                {
                    isLoading ? 'Loading...' :
                    articlesApropos.map((articlePropos) => 
                    <div className="text-apropos">
                        <h2>{articlePropos.titleApropos}</h2>
                        <p>{articlePropos.contentApropos}</p>                 
                    </div>
                    )
                }
            </section>
        </div>
    )
}

export default Apropos;
