import React, {useEffect, useState} from 'react';
import axios from 'axios';

export default function ArticlesClosing() {
    const [isLoading, setIsLoading] = useState(true)
    const [articles, setArticles] = useState(null)

    useEffect(() => {
        findAllArticlesClosing();
        findOneArticleClosing();
    },[])

    function findAllArticlesClosing() {
        axios.get('http://localhost:1337/api/articles')
        .then((response) => {
            setArticles(response.data.data)
            setIsLoading(false)
            // console.log(response.data.data[0].attributes.title)
        })
        .catch(error => {
            console.log(error)
        })
    }

    function findOneArticleClosing(id) {
        axios.get(`http://localhost:1337/api/articles/${id}`)
        .then((response) => {
            setArticles(response.data.data)
            setIsLoading(false)
            // console.log(response.data.data[0].attributes.title)
        })
        .catch(error => {
            console.log(error)
        })
    }

    return (
        <div>
            <ul>
                {
                    isLoading ? 'Loading...' : articles.map(article => <li key={article.attributes.id}>{article.attributes.title}</li>)
                }
            </ul>
        </div>
    )
}
